sp_theme.css_variables:
/* Mixins */

// transition
@mixin transition( $args... ) {
  -webkit-transition: $args;
  -moz-transition: $args;
  -ms-transition: $args;
  -o-transition: $args;
  transition: $args;
}

// transform
@mixin transform( $transforms ) {
  -moz-transform: $transforms;
  -o-transform: $transforms;
  -ms-transform: $transforms;
  -webkit-transform: $transforms;
  transform: $transforms;
}

// rotate
@mixin rotate( $deg ) {
	@include transform( rotate( $deg ) );
}

// scale
@mixin scale( $scale ) {
	@include transform( scale( $scale ) );
} 
// translate
@mixin translate( $x, $y ) {
	@include transform( translate( $x, $y ) );
}

//Anchor tags should be blue on hover
.team-member > label > a:hover { 
  color: #DF9483;
  font-weight: bold;
  margin-right: 5px;
}

.link:hover {
  color: #DF9483;
  font-weight: bold;
}
